const express=require("express"),fs=require("fs").promises,path=require("path"),bodyParser=require("body-parser"),crypto=require("crypto"),DATA_FILE=path.join(__dirname,"assets","records","users.json"),TMP_FILE=DATA_FILE+".tmp",app=express();function sanitizeInput(body){return{ID:String(Date.now()),Username:String(body.username||"").trim(),FullName:String(body.fullName||"").trim(),Role:body.role||"user",Barangay:String(body.barangay||"").trim(),Phone:String(body.phone||"").trim(),DateRegistered:(new Date).toLocaleDateString()}}app.use(bodyParser.json()),app.post("/api/users",async(req,res)=>{try{const{username:username,password:password,fullName:fullName,role:role,barangay:barangay,phone:phone}=req.body;if(!username||!password||!fullName)return res.status(400).json({error:"username, password and fullName required"});let users=[];try{const raw=await fs.readFile(DATA_FILE,"utf8");users=JSON.parse(raw),Array.isArray(users)||(users=[])}catch(err){users=[]}if(users.find(u=>u.Username===username))return res.status(409).json({error:"username exists"});const user=sanitizeInput({username:username,fullName:fullName,role:role,barangay:barangay,phone:phone});password&&(user.PasswordHash=crypto.createHash("sha256").update(String(password)).digest("hex")),users.push(user),await fs.mkdir(path.dirname(DATA_FILE),{recursive:!0}),await fs.writeFile(TMP_FILE,JSON.stringify(users,null,2),"utf8"),await fs.rename(TMP_FILE,DATA_FILE),res.status(201).json({success:!0,user:user})}catch(err){console.error("Error in /api/users:",err),res.status(500).json({error:"server error"})}}),app.get("/api/users",async(req,res)=>{try{const raw=await fs.readFile(DATA_FILE,"utf8");res.json(JSON.parse(raw))}catch(err){res.json([])}});const port=process.env.PORT||3e3;app.listen(port,()=>console.log(`User API listening on ${port}`));